RSpec.describe '/product_reports', type: :request, bullet: :skip_unused_eager_loading_check do
  # This should return the minimal set of attributes required to create a valid
  # ProductReport. As you add validations to ProductReport, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) do
    { content: 'Content', product: FactoryBot.create(:product), customer: FactoryBot.create(:customer), business: nil, staff: nil }
  end

  let(:invalid_attributes) do
    { content: '', product: FactoryBot.create(:product), customer: FactoryBot.create(:customer), business: nil, staff: nil }
  end

  let(:valid_parameters) do
    { content: 'Content', product_id: FactoryBot.create(:product).id }
  end

  let(:invalid_parameters) do
    { content: '', product_id: FactoryBot.create(:product).id }
  end

  before { login_as(FactoryBot.create(:admin), scope: :staff) }

  describe 'GET /index' do
    it 'renders a successful response' do
      ProductReport.create! valid_attributes
      get product_reports_url
      expect(response).to be_successful
    end
  end

  describe 'GET /show' do
    it 'renders a successful response' do
      product_report = ProductReport.create! valid_attributes
      get product_report_url(product_report)
      expect(response).to be_successful
    end
  end

  describe 'POST /create' do
    context 'with valid parameters' do
      it 'creates a new ProductReport' do
        expect do
          post product_reports_url, params: { product_report: valid_parameters }
        end.to change(ProductReport, :count).by(1)
      end
    end

    context 'with invalid parameters' do
      it 'does not create a new ProductReport' do
        expect do
          post product_reports_url, params: { product_report: invalid_parameters }
        end.to change(ProductReport, :count).by(0)
      end

      it "renders a successful response (i.e. to display the 'new' template)" do
        post product_reports_url, params: { product_report: invalid_parameters }
        expect(response).to be_successful
      end
    end
  end

  describe 'DELETE /destroy' do
    it 'destroys the requested product_report' do
      product_report = ProductReport.create! valid_attributes
      expect do
        delete product_report_url(product_report)
      end.to change(ProductReport, :count).by(-1)
    end

    it 'redirects to the product_reports list' do
      product_report = ProductReport.create! valid_attributes
      delete product_report_url(product_report)
      expect(response).to redirect_to(product_reports_url)
    end
  end
end
